INSERT INTO `events` (`id`, `nama`, `slug`, `image`, `deskripsi`, `tempat`, `tanggal`, `link_gmap`, `contact_person`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(1, 'Gembira X GenBI Mengabdi 2025', 'gembira-x-genbi-mengabdi-2025', NULL, '<p>&nbsp;Kegiatan <strong>Gembira X GenBI Mengabdi 2025</strong> merupakan wujud nyata semangat <em>Generasi Baru Indonesia</em> dalam mendukung peningkatan literasi keuangan digital dan motivasi pendidikan di kalangan generasi muda. Program ini akan dilaksanakan pada <strong>Februari 2025</strong> di <strong>Kantor Kecamatan Cilongok</strong>, dengan sasaran utama <strong>SMK Ma’arif Cilongok</strong> dan <strong>Anak Tidak Sekolah (ATS)</strong> di wilayah tersebut.</p><p><strong>Target peserta</strong> sebanyak <strong>100 orang</strong>, terdiri dari:</p><ul><li><strong>32 siswa</strong> kelas 10 dan 11,</li><li><strong>20 anggota OSIS</strong>, dan</li><li><strong>15 ATS</strong>.</li></ul><p><strong>Rangkaian acara</strong> mencakup:</p><ol><li><strong>Pemberian materi QRIS</strong>: Edukasi tentang penggunaan QRIS sebagai alat pembayaran digital yang aman, mudah, dan efisien.</li><li><strong>Pengenalan Ekonomi Syariah (Eksyar)</strong>: Memahami prinsip-prinsip ekonomi syariah dan aplikasinya dalam kehidupan sehari-hari.</li><li><strong>Motivasi Lanjut Sekolah</strong>: Memberikan dorongan dan pandangan kepada peserta, khususnya ATS, agar melanjutkan pendidikan demi masa depan yang lebih baik.</li></ol><p>Seluruh materi akan dibawakan oleh <strong>Pengurus GenBI</strong> yang memiliki kompetensi di bidangnya.</p><p>Kegiatan ini diharapkan mampu meningkatkan pemahaman peserta terkait literasi keuangan, membuka wawasan mereka tentang peluang di dunia pendidikan, serta memberikan motivasi untuk terus belajar dan berkembang. <strong>Bersama GenBI, kita bangun generasi muda yang mandiri, kreatif, dan siap menghadapi masa depan!</strong>&nbsp;</p>', 'Kantor Kecamatan Cilongok', '2025-02-01', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d4255.360226604899!2d109.13383607532163!3d-7.403352992606698!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e656100e3cef189%3A0x9c9787f3a5f5e5c3!2sKantor%20Kecamatan%20Cilongok!5e1!3m2!1sen!2sid!4v1735092666951!5m2!1sen!2sid', '+62882008874672', '2024-12-24 18:39:42', '2024-12-24 19:14:35', NULL),
	(2, 'Pelita X GenBI Mengabdi 2025', 'pelita-x-genbi-mengabdi-2025', NULL, '<p>&nbsp;Kegiatan <strong>Pelita X GenBI Mengabdi 2025</strong> merupakan inisiatif kolaboratif yang bertujuan untuk memberikan edukasi kesehatan dan pemberdayaan kepada <strong>anak disabilitas</strong> serta <strong>orang tua</strong> mereka di wilayah Cilongok. Program ini akan dilaksanakan pada <strong>8 Februari 2025</strong> di <strong>Balai Desa Pageraji</strong>, dengan target <strong>50 peserta</strong>, terdiri dari:</p><ul><li><strong>25 anak disabilitas</strong>, dan</li><li><strong>25 orang tua anak disabilitas</strong>.</li></ul><p><strong>Rangkaian acara</strong>:</p><ol><li><strong>Sosialisasi Perilaku Hidup Bersih dan Sehat (PHBS)</strong> serta <strong>Penyuluhan Gizi</strong>: Pemateri dari <strong>Puskesmas Kecamatan Cilongok</strong> akan memberikan edukasi tentang pentingnya menjaga kebersihan dan kesehatan, serta kebutuhan gizi yang sesuai bagi anak disabilitas.</li><li><strong>Sosialisasi CBPR (Community-Based Participatory Research)</strong>: Materi ini dibawakan oleh <strong>Pengurus GenBI</strong> (Tiffani dan Zaskia), bertujuan untuk memperkenalkan pendekatan partisipatif dalam memberdayakan komunitas untuk memecahkan permasalahan secara kolektif.</li></ol><p>Melalui kegiatan ini, diharapkan peserta dapat meningkatkan pemahaman tentang pentingnya kebersihan, kesehatan, dan pola makan yang baik, serta membangun kesadaran komunitas untuk lebih mendukung pemberdayaan anak disabilitas.</p><p><strong>Pelita X GenBI Mengabdi 2025</strong> menjadi bukti nyata bahwa langkah kecil bersama dapat menciptakan dampak besar dalam mewujudkan masyarakat yang inklusif, sehat, dan berdaya. <strong>Bersama GenBI, kita terangi masa depan yang lebih cerah!</strong>&nbsp;</p>', 'Balai Desa Pageraji', '2025-02-08', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d4255.284808639386!2d109.15592707532171!3d-7.411163892599074!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e6560ede2e07ea7%3A0x1c03dd29f5c2ee87!2sBalai%20Desa%20Pageraji!5e1!3m2!1sen!2sid!4v1735093911803!5m2!1sen!2sid', '+62882008874672', '2024-12-24 19:32:28', '2024-12-24 19:32:28', NULL),
	(3, 'ECOBUDIES X GenBI Mengabdi 2025', 'ecobudies-x-genbi-mengabdi-2025', NULL, '<p>&nbsp;Kegiatan <strong>ECOBUDIES X GenBI Mengabdi 2025</strong> adalah program edukasi dan aksi nyata untuk mendukung pelestarian lingkungan melalui pemberdayaan komunitas. Kegiatan ini menyasar <strong>Karang Taruna</strong> dan <strong>PKK Desa Pageraji</strong> sebagai mitra strategis dalam menggerakkan masyarakat menuju kehidupan yang lebih ramah lingkungan.</p><p>Program ini akan dilaksanakan pada <strong>15 Februari 2025</strong> di <strong>Kantor Balai Desa Pageraji</strong>, dengan target peserta <strong>30-40 orang</strong> yang berasal dari anggota <strong>PKK</strong> dan <strong>Karang Taruna</strong>.</p><p><strong>Rangkaian acara</strong>:</p><ol><li><strong>Sosialisasi Eco Enzyme</strong>: Edukasi mengenai manfaat eco enzyme sebagai produk ramah lingkungan yang dapat digunakan untuk berbagai keperluan, seperti pembersih alami dan pupuk organik.</li><li><strong>Praktik Pembuatan Eco Enzyme</strong>: Peserta akan langsung diajarkan cara membuat eco enzyme menggunakan limbah organik rumah tangga, dipandu oleh <strong>Komunitas Eco Enzyme</strong> dan panitia.</li></ol><p><strong>Reward dan Souvenir</strong>:</p><ul><li>Kelompok peserta dengan hasil terbaik dalam pembuatan eco enzyme akan mendapatkan <strong>alat masak</strong> sebagai hadiah.</li><li>Seluruh peserta akan membawa pulang <strong>sampel eco enzyme</strong> sebagai souvenir dan bukti nyata kontribusi mereka dalam menjaga lingkungan.</li></ul><p>Melalui kegiatan ini, diharapkan tercipta kesadaran kolektif di kalangan masyarakat Pageraji tentang pentingnya pengelolaan limbah organik dan penerapan gaya hidup berkelanjutan.</p><p><strong>ECOBUDIES X GenBI Mengabdi 2025</strong> adalah langkah kecil menuju dampak besar bagi lingkungan. <strong>Bersama, kita jaga bumi untuk generasi mendatang!</strong>&nbsp;</p>', 'Kantor Balai Desa Pageraji', '2025-02-15', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d4255.284808639386!2d109.15592707532171!3d-7.411163892599074!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e6560ede2e07ea7%3A0x1c03dd29f5c2ee87!2sBalai%20Desa%20Pageraji!5e1!3m2!1sen!2sid!4v1735093911803!5m2!1sen!2sid', '+62882008874672', '2024-12-24 20:01:59', '2024-12-24 20:01:59', NULL),
	(4, 'UMKM Menyala X GenBI Mengabdi 2025', 'umkm-menyala-x-genbi-mengabdi-2025', NULL, '<p>&nbsp;Kegiatan <strong>UMKM Menyala X GenBI Mengabdi 2025</strong> merupakan program pemberdayaan ekonomi yang berfokus pada peningkatan kapasitas pelaku <strong>UMKM</strong> di wilayah Pageraji. Program ini bertujuan untuk mendukung UMKM agar lebih adaptif terhadap perubahan teknologi dan mampu bersaing di era digital.</p><p>Kegiatan akan dilaksanakan pada <strong>Februari 2025</strong> di <strong>Balai Desa Pageraji</strong>, dengan target <strong>30 peserta</strong> yang berasal dari pelaku UMKM setempat.</p><p><strong>Rangkaian acara</strong>:</p><ol><li><strong>Sosialisasi QRIS dan Pembuatan QRIS</strong>: Peserta akan diberikan pemahaman tentang manfaat QRIS sebagai solusi pembayaran digital yang praktis dan aman. Selain itu, peserta juga akan langsung dibimbing untuk membuat QRIS bagi usaha mereka, dipandu oleh <strong>Pengurus GenBI</strong> dan <strong>Dinkerkop dan UKM</strong>.</li><li><strong>Pembinaan UMKM</strong>: Materi pembinaan akan mencakup pengelolaan usaha, pemasaran digital, hingga strategi pengembangan produk.</li><li><strong>Pemberitahuan Stand UMKM dan Lomba UMKM</strong>: Informasi tentang peluang untuk memamerkan produk UMKM di stand dan berkompetisi dalam lomba UMKM akan disampaikan kepada peserta.</li></ol><p><strong>Formulir Pendataan UMKM</strong> akan disebar saat kegiatan <strong>Gembira X GenBI Mengabdi 2025</strong> dan dikumpulkan pada saat sosialisasi QRIS untuk memastikan data peserta yang akurat.</p><p><strong>Reward dan Souvenir</strong>:</p><ul><li>Peserta dengan performa terbaik akan mendapatkan <strong>teflon</strong> sebagai hadiah.</li><li>Seluruh peserta akan menerima <strong>tumbler</strong> sebagai souvenir kegiatan.</li></ul><p>Melalui <strong>UMKM Menyala X GenBI Mengabdi 2025</strong>, diharapkan pelaku UMKM tidak hanya memahami teknologi keuangan digital tetapi juga mampu mengoptimalkan usaha mereka untuk mendukung pertumbuhan ekonomi lokal. <strong>Bersama GenBI, kita nyalakan semangat UMKM menuju masa depan yang lebih cerah!</strong>&nbsp;</p>', 'Balai Desa Pageraji', '2025-02-22', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d4255.284808639386!2d109.15592707532171!3d-7.411163892599074!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e6560ede2e07ea7%3A0x1c03dd29f5c2ee87!2sBalai%20Desa%20Pageraji!5e1!3m2!1sen!2sid!4v1735093911803!5m2!1sen!2sid', '+62882008874672', '2024-12-24 20:03:59', '2024-12-24 20:03:59', NULL),
	(5, 'Bazar dan Puncak GenBI Mengabdi 2025', 'bazar-dan-puncak-genbi-mengabdi-2025', NULL, '<p>&nbsp;<strong>Bazar dan Puncak GenBI Mengabdi 2025</strong> adalah acara penutup yang menjadi puncak dari seluruh rangkaian kegiatan <strong>GenBI Mengabdi</strong> di tahun 2025. Acara ini dirancang sebagai momen kebersamaan untuk merayakan pencapaian dan memberikan apresiasi kepada seluruh pihak yang telah berkontribusi.</p><p>Kegiatan puncak ini akan menghadirkan beragam acara menarik dan interaktif, di antaranya:</p><ol><li><strong>Senam Sehat</strong>: Mengawali acara dengan aktivitas fisik untuk menjaga kebugaran sekaligus membangun semangat kebersamaan.</li><li><strong>Simbolis Penutupan</strong>: Upacara resmi untuk menandai berakhirnya rangkaian kegiatan <strong>GenBI Mengabdi 2025</strong>, diikuti dengan simbolisasi oleh perwakilan GenBI.</li><li><strong>Kentongan</strong>: Pertunjukan khas daerah sebagai bentuk penghormatan pada budaya lokal.</li><li><strong>Panggung Bebas</strong>: Ruang ekspresi bagi peserta untuk menampilkan kreativitas mereka, mulai dari musik, tarian, hingga seni pertunjukan lainnya.</li><li><strong>Bazar atau Open Stand UMKM</strong>: Para pelaku UMKM akan memamerkan dan menjual produk mereka, sebagai upaya mendukung ekonomi lokal dan memperkenalkan produk unggulan kepada masyarakat.</li><li><strong>Lapak Maca</strong> <em>(Tahap Konfirmasi)</em>: Area khusus untuk membaca buku sebagai bentuk kampanye literasi di tengah masyarakat.</li><li><strong>Penayangan Video Kreatif SMK</strong>: Video hasil karya siswa <strong>SMK Ma’arif Cilongok</strong> yang sebelumnya telah mengikuti kompetisi akan diputar untuk mengapresiasi kreativitas mereka.</li><li><strong>Pengumuman All Kejuaraan</strong>: Pengumuman pemenang berbagai kompetisi, termasuk:<ul><li><strong>Lomba UMKM</strong>,</li><li><strong>Video Kreatif SMK</strong>, dan</li><li><strong>Panggung Bebas</strong>.</li></ul></li></ol><p><strong>Bazar dan Puncak GenBI Mengabdi 2025</strong> tidak hanya menjadi perayaan akhir tetapi juga menjadi ajang untuk menginspirasi, memberdayakan, dan mempererat hubungan antar-komunitas. Dengan semangat kebersamaan dan inklusivitas, acara ini diharapkan meninggalkan kesan mendalam serta manfaat yang berkelanjutan bagi masyarakat. <strong>Bersama GenBI, kita terus melangkah maju, membangun masa depan yang lebih baik!</strong>&nbsp;</p>', 'Pageraji', '2025-03-01', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d10120.717903546325!2d109.15267318629996!3d-7.416038444020831!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e6560eb839cf5f3%3A0x5027a76e35506f0!2sPageraji%2C%20Cilongok%2C%20Banyumas%20Regency%2C%20Central%20Java!5e1!3m2!1sen!2sid!4v1735095975662!5m2!1sen!2sid', '+62882008874672', '2024-12-24 20:07:30', '2024-12-24 20:07:30', NULL);
INSERT INTO `galeris` (`id`, `title`, `slug`, `waktu`, `tempat`, `thumbnail`, `deskripsi`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(1, 'Kegiatan 1', 'kegiatan-1', '2024-12-05', 'Biasa', 'galeri/01JESP2YWQJ7HER5C006M0T5G8.png', 'Proin curae morbi felis dictumst nunc arcu lacus. Fringilla ornare aenean nascetur lobortis accumsan amet est tellus. Posuere at dapibus vehicula auctor himenaeos sit congue turpis libero malesuada.', '2024-12-10 18:42:10', '2024-12-24 18:26:29', '2024-12-24 18:26:29'),
	(2, 'Kegiatan 2', 'kegiatan-2', '2024-09-18', 'Tempat 3', 'galeri/01JESP6KNQD36H0NFVB5HP6XJS.jpg', 'Quis vulputate aptent felis lobortis urna congue iaculis sodales ornare interdum. Non blandit ac inceptos in vivamus tristique. Posuere dictum si quisque rutrum duis lacinia. Facilisis suspendisse si platea imperdiet faucibus sollicitudin potenti nulla convallis aenean. Fermentum dis amet lacus ut dictum pretium nisi.', '2024-12-10 18:44:10', '2024-12-24 18:26:29', '2024-12-24 18:26:29'),
	(3, 'Kegiatan 3', 'kegiatan-3', '2024-11-14', 'Biasa 7', 'galeri/01JESPD6BNDAVC1BZ8TRA8GQDC.png', 'Leo tortor consectetuer pellentesque venenatis magnis potenti suscipit urna pretium nascetur vivamus. Quisque feugiat commodo pellentesque volutpat eleifend malesuada. Orci torquent vel elit etiam duis porttitor. Taciti mus curabitur magnis enim vivamus netus sem facilisis laoreet imperdiet. Cras nascetur dis consectetuer egestas nam semper vulputate vel taciti himenaeos nisi.', '2024-12-10 18:47:45', '2024-12-24 18:26:29', '2024-12-24 18:26:29'),
	(4, 'Kegiatan 4', 'kegiatan-4', '2024-12-26', 'Biasa 10', 'galeri/01JESPGC3PAXVB23DNGCR261CQ.jpg', 'Vestibulum non dignissim condimentum libero id congue pede augue et natoque. Sed dignissim facilisis risus nascetur congue aenean dolor quam. Malesuada cras maecenas placerat suspendisse sed sapien. Convallis tellus elit vehicula pellentesque mi metus litora lacinia orci hac enim. Bibendum platea ornare accumsan mi mus.', '2024-12-10 18:49:30', '2024-12-24 18:26:29', '2024-12-24 18:26:29'),
	(5, 'GenBI Health Fest 2024', 'genbi-health-fest-2024', '2024-11-23', 'Balaidesa Purwokerto Kidul', 'galeri/01JFXPVAJ3KPNP049REGWAWYDR.jpg', 'GenBI Health Fest x Geber DBD adalah sebuah kegiatan yang mengedepankan kepedulian terhadap kesehatan masyarakat dengan fokus pada pencegahan Demam Berdarah Dengue (DBD). Acara ini menggabungkan edukasi, pemeriksaan kesehatan, serta gerakan pemberantasan sarang nyamuk untuk menciptakan lingkungan yang sehat dan bebas DBD. Kegiatan ini juga melibatkan berbagai elemen masyarakat sebagai bentuk kolaborasi dalam meningkatkan kesadaran dan aksi nyata untuk kesehatan bersama.', '2024-12-24 18:28:08', '2024-12-24 18:28:08', NULL),
	(6, 'GenBI Ecocreative X Berakal 2024', 'genbi-ecocreative-x-berakal-2024', '2024-09-15', 'Aula Balaidesa Kalisalak', 'galeri/01JFY1X08TEX18428YZJZJMRPA.jpg', 'GenBI dengan bangga menyelenggarakan "Ecocreative X Berakal 2024," sebuah acara yang menggabungkan kreativitas dan tanggung jawab lingkungan. Mengangkat tema "Kreasi Hijau dari Alam: Menghadirkan Keindahan dan Kesadaran melalui Teknik Seni Cetak Berbasis Lingkungan," acara ini bertujuan untuk memberikan platform kepada masyarakat untuk mengeksplorasi teknik seni cetak ramah lingkungan dengan menggunakan bahan-bahan alami, seperti daun dan bunga. Kegiatan ini juga bertujuan untuk meningkatkan kesadaran tentang pentingnya praktik berkelanjutan dalam menjaga kelestarian alam.\n\nAcara yang berlangsung pada 15 September 2024 di Aula Balaidesa Kalisalak, Kedung Banteng, ini menghadirkan Suciatin, pemilik Srayan Makarya, sebagai pemateri utama yang memberikan pelatihan mengenai teknik Ecoprint, seni cetak berbasis lingkungan. Silviani Hidayah bertindak sebagai moderator dan memandu acara yang dihadiri oleh 50 peserta, termasuk ibu-ibu PKK, karang taruna desa, serta perwakilan dari GenBI. Selain sesi pelatihan Ecoprint, acara ini juga mencakup pemaparan materi tentang QRIS dan aksi penanaman pohon sebagai simbol komitmen terhadap pelestarian lingkungan.\n\nProgram ini merupakan bagian dari Departemen Lingkungan GenBI yang bertujuan untuk meningkatkan kreativitas masyarakat dengan memanfaatkan sumber daya alam yang ada, menciptakan karya seni yang ramah lingkungan dan berkelanjutan. Melalui kegiatan ini, GenBI berharap dapat memperkenalkan nilai-nilai keberlanjutan serta menunjukkan bahwa alam merupakan sumber inspirasi yang tak ternilai untuk menghasilkan karya seni yang indah dan bermakna.', '2024-12-24 21:41:17', '2024-12-24 21:41:17', NULL);
