INSERT INTO `strukturs` (`id`, `nama_lengkap`, `type`, `jabatan`, `periode`, `universitas`, `quote`, `foto`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(1, 'Mochamad Fajar Arianto ', 'president', 'Presiden Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', 'Percayalah bahwa walau hanya dengan sedikit langkah akan banyak memberi perubahan ke arah lebih cerah ', 'struktur/01JEDJ2BQJEPYJJ9WPSB2GPEMV.png', '2024-11-28 21:18:43', '2024-12-06 01:41:03', NULL),
	(2, 'Nia Nurmayanti', 'president', 'Presiden Komisariat UMP', '2024-2025', 'Universitas Muhammadiyah Purwokerto', 'Tidak semua menjadi komandan,  tentu harus ada prajuritnya, bukan besar kecil tugas yang menjadikan  tinggi rendahnya nilai dirimu, jadilah dirimu sebaik-baik dirimu sendiri', 'struktur/01JEDJCB6Y3HYHH90EAWGPE9VW.png', '2024-11-28 21:18:43', '2024-12-06 01:46:30', NULL),
	(3, 'Stevani Aprilya Pratama', 'president', 'Presiden Komisariat UIN', '2024-2025', 'Universitas Islam Negeri Prof. K.H. Saifuddin Zuhri', 'Semua yang ada didepan itu bukan suatu kebetulan, beradablah dan terus bersungguh-sungguh dalam memulai sesuatu', 'struktur/01JEDJFH9VR8YPA1WK1GDMNJQE.png', '2024-11-28 21:18:43', '2024-12-06 01:48:15', NULL),
	(4, 'Maya Amalia Kurniati', 'secretary', 'Sekretaris Umum Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', 'Teruslah melangkah sampai semua harapan menjadi nyata', 'struktur/01JEDJH5F6QXQK2CTSJWDRZ79W.png', '2024-11-28 21:18:43', '2024-12-06 01:49:08', NULL),
	(5, 'Vitriyah Arafah Surachman', 'secretary', 'Sekretaris Umum Komisariat UMP', '2024-2025', 'Universitas Muhammadiyah Purwokerto', '"Man Jadda Wa Jada", Barangsiapa yang bersungguh-sungguh pasti akan berhasil', 'struktur/01JEDJK0GEJSHWGQFKKMX7BN45.png', '2024-11-28 21:18:43', '2024-12-06 01:50:09', NULL),
	(6, 'Asih Saferia Meilani', 'secretary', 'Sekretaris Umum Komisariat UIN', '2024-2025', 'Universitas Islam Negeri Prof. K.H. Saifuddin Zuhri', 'Jadilah pribadi yang bermanfaat dan teruslah berbuat kebaikan', 'struktur/01JEDJMF0E0209YJYH10XJ3CPF.png', '2024-11-28 21:18:43', '2024-12-06 01:50:56', NULL),
	(7, 'Ika Safitri', 'treasure', 'Bendahara Umum Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', 'Berani mencoba hal baru adalah cara terbaik untuk mengeksplor potensi diri dan menemukan hal-hal yang kita tak tahu sebelumnya.', 'struktur/01JEDJVH968QBJ84XGPHWNS4DT.png', '2024-11-28 21:18:43', '2024-12-06 01:54:48', NULL),
	(8, 'Hana Pratiwi', 'treasure', 'Bendahara Umum Komisariat UMP', '2024-2025', 'Universitas Muhammadiyah Purwokerto', 'Sebaik-baik manusia adalah yang paling bermanfaat bagi orang lain', 'struktur/01JEDJN0ZVQ8BTYE588FN8GN0K.png', '2024-11-28 21:18:43', '2024-12-06 01:51:15', NULL),
	(9, 'Dewi Fatimah', 'treasure', 'Bendahara Umum Komisariat UIN', '2024-2025', 'Universitas Islam Negeri Prof. K.H. Saifuddin Zuhri', 'لَا تَحْتَقِرْ مَنْ دُوْنَكَ فَلِكُلِّ شَىءٍ مَزِيَّةٌ\n\nJanganlah menghina orang yang lebih rendah darimu, karena setiap sesuatu memiliki kelebihan.', 'struktur/01JEDJN69YV1QW0DAMRAFKE2GV.png', '2024-11-28 21:18:43', '2024-12-06 01:51:20', NULL),
	(10, 'Huwaida Nurjanah', 'deputy', 'Deputi Pendidikan Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', 'Kesempatan hanya datang sekali, berusahalah semaksimal mungkin agar tidak menyesal dikemudian hari', 'struktur/01JEDKH0QNH940XK8CJHE8EZ06.png', '2024-11-28 21:18:43', '2024-12-06 02:06:32', NULL),
	(11, 'Nabil Umar Wisanggeni', 'deputy', 'Deputi Pendidikan Komisariat UMP', '2024-2025', 'Universitas Muhammadiyah Purwokerto', '"Have a Comfortable day. Don\'t try hard to be happy. Just let it go and feel how great it is" - Taeyong NCT', 'struktur/01JEDK17Y1PB84Y9F9JZN58FCP.png', '2024-11-28 21:18:43', '2024-12-06 01:57:55', NULL),
	(12, 'Mely Nur Arifah', 'deputy', 'Deputi Pendidikan Komisariat UIN', '2024-2025', 'Universitas Islam Negeri Prof. K.H. Saifuddin Zuhri', '"Di GenBI, setiap langkah adalah kesempatan untuk tumbuh lebih baik, memberi lebih banyak, dan menginspirasi lebih banyak orang." ', 'struktur/01JEDK1GA30HDTCHAE5YC0VK1G.png', '2024-11-28 21:18:43', '2024-12-06 01:58:03', NULL),
	(13, 'Rahayu Nur Rahmawati', 'deputy', 'Deputi Ekonomi Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', '"karena pelaut hebat tak pernah lahir di laut yang tenang" - HIVI!', 'struktur/01JEDK4PDRZH51FH5NA6PTKFN4.png', '2024-11-28 21:18:43', '2024-12-06 01:59:48', NULL),
	(14, 'Aisah Nurfiani', 'deputy', 'Deputi Ekonomi Komisariat UMP', '2024-2025', 'Universitas Muhammadiyah Purwokerto', 'Kebahagiaan dapat dicapai dengan menerima apa adanya tanpa mempertanyakan mengapa-Marcus Aurelius', 'struktur/01JEDK5X36CBGV9AYABNAXD2FS.png', '2024-11-28 21:18:43', '2024-12-06 02:00:28', NULL),
	(15, 'Fajri Mulia Ningsih', 'deputy', 'Deputi Ekonomi Komisariat UIN', '2024-2025', 'Universitas Islam Negeri Prof. K.H. Saifuddin Zuhri', 'Nothing is easy but nothing is impossible', 'struktur/01JEDK5ZBH5EHYYYNWP8QZS83W.png', '2024-11-28 21:18:43', '2024-12-06 02:00:30', NULL),
	(16, 'Anisa Ramadani', 'deputy', 'Deputi Ligkungan Komisariat UNSOED', '2024-2025', 'Universitas Jenderal Soedirman', 'KOSONG', 'struktur/01JEDK7XFVEF9F9G4DDG2PK5MD.png', '2024-11-28 21:18:43', '2024-12-06 02:01:34', NULL),
